-- 1. Retrieve customer ordersSELECT c.CompanyName, oh.SalesOrderID, oh.TotalDueFROM SalesLT.Customer AS cINNER JOIN SalesLT.SalesOrderHeader AS ohON oh.CustomerID = c.CustomerID;-- 2. Retrieve customer orders with addressesSELECT c.CompanyName, oh.SalesOrderID, oh.TotalDue, ISNULL(a.AddressLine1,'') + ', ' + ISNULL(a.AddressLine2,'') AS FullStreetAddress, a.City, a.StateProvince, a.PostalCode, a.CountryRegionFROM SalesLT.Customer AS cINNER JOIN SalesLT.SalesOrderHeader AS ohON oh.CustomerID = c.CustomerIDLEFT JOIN SalesLT.CustomerAddress AS caON ca.CustomerID = c.CustomerIDLEFT JOIN SalesLT.Address AS aON a.AddressID = ca.AddressIDWHERE ca.AddressType = 'Main Office';